import request from "@/utils/index";

const {ElMessage} = require("element-plus");
export default {
    name: "login",
    data() {
        return {
            identity: "",
            form: {
                username: "",
                password: "",
                identity: "",
            },
            rules: {
                username: [
                    {required: true, message: "请输入账号", trigger: "blur"},
                ],
                password: [{required: true, message: "请输入密码", trigger: "blur"}],
                identity: [{required: true, message: "请选择身份", trigger: "blur"}],
            },
        };
    },
    computed: {
        disabled() {
            const {username, password, identity} = this.form;
            return Boolean(username && password && identity);
        },
    },
    methods: {
        login() {
            this.$refs.form.validate((valid) => {
                if (valid) {
                    console.log(this.form.username)
                    console.log(this.form.password)
                    console.log(this.form.identity)
                    this.identity = this.form.identity;
                    const temp = "/" + this.identity + "/login"
                    co
                    request.post("/" + this.identity + "/login", this.form).then((res) => {
                        if (res.code === "200") {
                            console.log("chengg")
                            ElMessage({
                                message: "登陆成功",
                                type: "success",
                            });
                            // 登陆成功跳转主页
                            // window.sessionStorage.setItem("user", JSON.stringify(res.data));
                            // window.sessionStorage.setItem("identity", JSON.stringify(this.form.identity));
                            // this.$router.replace({path: "/home"});
                            console.log(res.data)
                        } else {
                            console.log("fail")
                            ElMessage({
                                message: res.msg,
                                type: "error",
                            });
                        }
                    });
                }
            });
        },
    },
};
